#!/usr/bin/env zsh
# PURPOSE:
# Answer David if distant enzymes has non of the features selected. We find that many of them have the default value -1 for all selected features.
# REQUIRES:
# ../seqs.tmp.tsv
# ../speciesSeqSelect.tmp
# which can be generated by running ../traintest.sh
encode_features.py -k -i enzyme --aa seq --aa-encoding `git root`/data/NCBI/matchAmb.tsv < ../seqs.tmp.tsv |
    mlr --tsv cut -f enzyme,`tr "\n" , < ../speciesSeqSelect.tmp` |
    grep -v "\t1\t" | mlr --tsv cut -f enzyme | sed 1d > enzymesNeverVary.acc
